import React from 'react';
import ReactDOM from 'react-dom';
import Wave from 'react-wavify';
import styled from '@emotion/styled';

import './Styles.css';

const WaveContainer = styled.div`
  position: fixed;
  left: 0;
  right: 0;
  top: -5px;
  height: ${(props) => props.level + 'vh'};
  display: flex;
  z-index: -1;
  transform: rotate(180deg);
`;


function Wavers() {
      return(


                  <div style={{ marginTop: "-200px" }}>
                    <WaveContainer level={85}>
                      <Wave
                        fill="red"
                        paused={false}
                        opacity="1.0"
                        options={{
                          height: 20,
                          amplitude: 10,
                          speed: 0.2,
                          points: 3,
                        }}
                      />
                    </WaveContainer>
                    <WaveContainer level={85}>
                      <Wave
                        fill="blue"
                        opacity="1.0"
                        paused={false}
                        options={{
                          height: 75,
                          amplitude: 20,
                          speed: 0.3,
                          points: 2,
                        }}
                      />
                    </WaveContainer>
                    <WaveContainer level={85}>
                      <Wave
                        fill="var(--top)"
                        paused={false}
                        opacity="1.0"
                        options={{
                          height: 45,
                          amplitude: 30,
                          speed: 0.1,
                          points: 4,
                        }}
                      />
                    </WaveContainer>
                  </div>
                );
              }

              const rootElement = document.getElementById('root');
              ReactDOM.render(<Wavers />, rootElement);


export default Wavers;
