import React, {useRef, useState} from 'react';
import ReactDOM from 'react-dom';
import Wave from 'react-wavify';
import styled from '@emotion/styled';
import waveMode from './Timer';
import IsPaused from './Timer';
import nextMode from './Timer';
import secondsLeft from './Timer';

import './Styles.css';

const WaveContainer = styled.div`
  position: fixed;
  left: 0;
  right: 0;
  top: -5px;
  height: ${(props) => props.level + 'vh'};
  display: flex;
  z-index: -1;
  transform: rotate(180deg);
`;



function Wavers() {
const [colorVar, setColorVar] = useState('false');
var waveColor = '';

// const colorVar='';
// if (waveMode === 'work' 
// ? colorVar='var(--green)'
// : colorVar='var(--red)')

const ChangeColor = () => {
      if (waveMode === 'work') {
      setColorVar(true)
      waveColor='var(--red)';
 } else { 
       setColorVar(false);
 }
      };

      return(
                  <div style={{ marginTop: "-200px" }}>
                    <WaveContainer level={100}>
                      <Wave
                        //fill="#00b6ad
                        fill={waveColor}
                        paused={false}
                        opacity="1.30"
                        options={{
                          height: 90,
                          amplitude: 10,
                          speed: 0.2,
                          points: 3,
                        }}  
                      />
                    </WaveContainer>
                    <WaveContainer level={90}>
                      <Wave
                        fill="#00959e"
                        opacity="1.80"
                        paused={false}
                        options={{
                          height: 75,
                          amplitude: 20,
                          speed: 0.3,
                          points: 2,
                        }}
                      />
                    </WaveContainer>
                    <WaveContainer level={80}>
                      <Wave
                        fill="#01838a"
                        paused={false}
                        opacity="1.5"
                        options={{
                          height: 45,
                          amplitude: 30,
                          speed: 0.1,
                          points: 4,
                        }}
                      />
                    </WaveContainer>
                  </div>
                );
              }

              const rootElement = document.getElementById('root');
              ReactDOM.render(<Wavers />, rootElement);


export default Wavers;
